import React from "react";
import { Wizard, Steps, Step } from 'react-albus';
import Navigation from './Navigation';
import { useFormik } from "formik";
import Loader from "./LoadingComponent";
import ErrorComponent from "./ErrorComponent";

const lettersOnlyRegex = /[a-zA-Z]+/;
const emailRegex = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,})+$/;
const numbersOnlyRegex = /^\d+$/;


const ContactForm = () => {
    const [hasError, setError] = React.useState(false);
    const [formIsSubmitting, setFormIsSubmitting] = React.useState(false);

    const formik = useFormik({
        initialValues: { 
            name: "",
            email: "",
            telephone: "",
            address: "",
            experience: "",
         },
        onSubmit: (values, errors) => {
            setFormIsSubmitting(true);
            const nameError = lettersOnlyRegex.test(values.name.toLowerCase());
            const emailError = emailRegex.test(values.email.toLowerCase());
            const telephoneError = numbersOnlyRegex.test(values.telephone.toString().toLowerCase());
            const experienceError = numbersOnlyRegex.test(values.experience.toString().toLowerCase());

            if ((values.name === "" || nameError) || (values.email === "" || emailError ) || (values.telephone === "" || telephoneError ) || values.address === "" || (values.experience === "" || experienceError)) {
                setError(true);
            } else {
                fetch('https://formspree.io/f/xaylazjw', {
                    method: 'POST',
                  }).then(function(response) {
                    console.log(response)
                    return response.json();
                  });
            }

            setFormIsSubmitting(false);

        }
      });

      React.useEffect( ()=> {
        setTimeout(() => {
            setError(false);
        }, 2000);
      }, [hasError]);

    return (
        <>
            <form className="form" onSubmit={formik.handleSubmit}>
                <Wizard>
                    <input type="hidden" name="id" value="123456"/>
                    {!formIsSubmitting &&
                    <Steps>
                        <Step id="name">
                            <div className="form-group">
                                <label className="form-group__label">Name</label>
                                <input className="form-group__input" type="text" name="name" onChange={formik.handleChange} value={formik.values.name}/>
                            </div>
                        </Step>
                        
                        <Step id="email">
                            <div className="form-group">
                                <label className="form-group__label">Email</label>
                                <input className="form-group__input" type="text" name="email" onChange={formik.handleChange} value={formik.values.email}/>
                            </div>
                        </Step>
                        
                        <Step id="phoneNumber">
                            <div className="form-group">
                                <label className="form-group__label">Telephone number</label>
                                <input className="form-group__input" type="number" name="telephone" onChange={formik.handleChange} value={formik.values.telephone}/>
                            </div>
                        </Step>
                        
                        <Step id="address">
                            <div className="form-group">
                                <label className="form-group__label">Address</label>
                                <input className="form-group__input" type="text" name="address" onChange={formik.handleChange} value={formik.values.address}/>
                            </div>
                        </Step>
                        
                        <Step id="experience">
                            <div className="form-group">
                                <label className="form-group__label">Number of years experience</label>
                                <input className="form-group__input" type="number" name="experience" onChange={formik.handleChange} value={formik.values.experience}/>
                            </div>
                        </Step>
                        
                        <Step id="topics">
                            <div className="form-group">
                                <label className="form-group__label">Favourite web development topics</label>
                                <input className="form-group__checkbox" type="checkbox" name="faves[]"/> Hex colours<br />
                                <input className="form-group__checkbox" type="checkbox" name="faves[]"/> Accessibility<br />
                                <input className="form-group__checkbox" type="checkbox" name="faves[]"/> Node modules<br />
                                <input className="form-group__checkbox" type="checkbox" name="faves[]"/> Javascript frameworks<br />
                                <input className="form-group__checkbox" type="checkbox" name="faves[]"/> BBQ<br />
                            </div>
                        </Step>
                        
                        <Step id="why">
                            <div className="form-group">
                                <label className="form-group__label">Why I want to work at Etch</label>
                                <textarea name="why" className="form-group__input form-group__input-textarea"></textarea>
                            </div>
                        </Step>
                    </Steps>
                    }
                    {hasError &&
                        <ErrorComponent errorTitle={"Ooops something's gone wrong"} errorBody={"Please go back through the form and make sure you have filled everything out."} />
                    }

                    {!formIsSubmitting &&
                        <Navigation/> 
                    }

                    {formIsSubmitting && 
                        <Loader/>
                    }
                </Wizard>
            </form>
        </>
    );
};

export default ContactForm;
