import React from "react";

const ErrorComponent = (errorTitle: string, errorBody: string) => {
    return (
        <div className="alert alert-warning">
            <h3>{errorTitle}</h3>
            <p>{errorBody}</p>
        </div>
    );
}
export default ErrorComponent;
